async function run(){
  try {
    await Promise.reject(new Error("fail"));
  } catch(e){
    console.log(e.message);
  }
}
run();

const wait = ms => new Promise(r=>setTimeout(r,ms));
async function seq(){
  await wait(10);
  console.log("first");
  await wait(10);
  console.log("second");
}
seq();

const a=Promise.resolve(1);
const b=Promise.resolve(2);
(async()=>{
  const [x,y]=await Promise.all([a,b]);
  console.log(x+y);
})();

let tries=0;
async function task(){
  tries++;
  if(tries===1) throw new Error("retry");
  return "success";
}
(async()=>{
  try{ await task(); }catch{ console.log(await task()); }
})();

