const marks = { math: 90, sci: 85, eng: 95 };
const total = Object.values(marks).reduce((a, b) => a + b, 0);
console.log(total);

const user = { id: 1, name: "Amit" };
for (const [k, v] of Object.entries(user)) {
  console.log(k, v);
}

const params = { q: "js", page: 2 };
const qs = new URLSearchParams(Object.entries(params)).toString();
console.log(qs);

const mixed = { a: 1, b: "x", c: 3, d: true };
const onlyNums = Object.fromEntries(Object.entries(mixed).filter(([, v]) => typeof v === "number"));
console.log(onlyNums);

const roleById = { 1: "admin", 2: "user" };
const idByRole = Object.fromEntries(Object.entries(roleById).map(([k, v]) => [v, k]));
console.log(idByRole);

const env = { NODE_ENV: "dev", PORT: 3000 };
const lines = Object.entries(env).map(([k, v]) => `${k}:${v}`);
console.log(lines);

const form = { first: "A", last: "B" };
const valid = Object.values(form).every(v => typeof v === "string" && v.trim() !== "");
console.log(valid);

const input = { a: 1, b: 2 };
const upper = Object.fromEntries(Object.entries(input).map(([k, v]) => [k.toUpperCase(), v]));
console.log(upper);
