import { db } from '@/lib/database';

export default async function Users() {
  // Direct database query
  const users = await db.query('SELECT * FROM users');

  return (
    <div>
      {users.map(user => (
        <div key={user.id}>{user.name}</div>
      ))}
    </div>
  );
}
