code:
-- Get all users
SELECT * FROM users;

-- Get all admin users
SELECT * FROM users WHERE role = 'admin';

-- Get all customers
SELECT * FROM users WHERE role = 'customer';

-- Get all products under 'Electronics'
SELECT * FROM products WHERE category = 'Electronics';

-- Get products priced above 500
SELECT * FROM products WHERE price > 500;

-- Get products with stock less than 10
SELECT * FROM products WHERE stock_quantity < 10;

-- Find product by SKU
SELECT * FROM products WHERE sku = 'PROD-123';

-- Get all orders with status = 'processing'
SELECT * FROM orders WHERE status = 'processing';

example:
-- Get all payments with status = 'completed'
SELECT * FROM payments WHERE status = 'completed';

-- Get all returns that are still requested
SELECT * FROM returns WHERE status = 'requested';

-- Find all carts created in last 7 days
SELECT * FROM carts 
WHERE created_at >= NOW() - INTERVAL '7 days';

-- Get all stock reservations expiring in next 5 minutes
SELECT * FROM stock_reservations 
WHERE expires_at <= NOW() + INTERVAL '5 minutes'
  AND expires_at > NOW();

-- Get users created today
SELECT * FROM users 
WHERE DATE(created_at) = CURRENT_DATE;

-- Find all products without category
SELECT * FROM products WHERE category IS NULL;

-- Get all orders using COD
SELECT * FROM orders WHERE payment_method = 'COD';
