code:
-- Basic SELECT
SELECT * FROM users;

-- Specific columns
SELECT name, email, age FROM users;

-- WHERE clause
SELECT * FROM products 
WHERE price > 100 AND stock > 0;

-- ORDER BY
SELECT * FROM orders 
ORDER BY created_at DESC;

-- LIMIT and OFFSET
SELECT * FROM posts 
ORDER BY created_at DESC 
LIMIT 10 OFFSET 20;

-- DISTINCT
SELECT DISTINCT category FROM products;
