example:
-- IN with subquery
SELECT * FROM products 
WHERE category_id IN (
  SELECT id FROM categories WHERE active = true
);

-- NOT IN
SELECT * FROM users 
WHERE id NOT IN (
  SELECT DISTINCT user_id FROM orders
);

-- Correlated subquery
SELECT 
  p.name,
  p.price,
  (SELECT COUNT(*) FROM reviews r WHERE r.product_id = p.id) as review_count
FROM products p;
