import React from 'react';

export function StatsCardGrid({ items }) {
  return (
    <section className="grid grid-cols-1 gap-4 sm:grid-cols-2 xl:grid-cols-4">
      {items.map((item) => (
        <article key={item.id} className="rounded-xl border bg-white p-4 shadow-sm">
