import React, { useEffect, useRef } from 'react';

export function ModalWithFocusTrap({ open, onClose, children }) {
  const ref = useRef(null);

  useEffect(() => {
    if (!open) return;
    const root = ref.current;
    const focusables = root?.querySelectorAll('button,[href],input,select,textarea,[tabindex]:not([tabindex="-1"])');
    focusables?.[0]?.focus();

    const onKeyDown = (e) => {
      if (e.key === 'Escape') onClose();
    };
    document.addEventListener('keydown', onKeyDown);
    return () => document.removeEventListener('keydown', onKeyDown);
  }, [open, onClose]);

  if (!open) return null;
  return (
    <div className="fixed inset-0 z-50 grid place-items-center bg-black/50" role="dialog" aria-modal="true">
      <div ref={ref} className="w-full max-w-lg rounded-xl bg-white p-5">
        {children}
      </div>
    </div>
  );
}

import React, { useMemo, useState } from 'react';

export function VirtualizedList({ items, rowHeight = 44, viewportHeight = 360 }) {
  const [scrollTop, setScrollTop] = useState(0);
  const totalHeight = items.length * rowHeight;

  const { start, end } = useMemo(() => {
    const s = Math.floor(scrollTop / rowHeight);
    const visible = Math.ceil(viewportHeight / rowHeight);
    return { start: Math.max(0, s - 5), end: Math.min(items.length, s + visible + 5) };
  }, [scrollTop, rowHeight, viewportHeight, items.length]);

  const visibleItems = items.slice(start, end);

  return (
    <div
      className="overflow-auto rounded border"
      style={{ height: viewportHeight }}
      onScroll={(e) => setScrollTop(e.currentTarget.scrollTop)}
    >
      <div style={{ height: totalHeight, position: 'relative' }}>
        {visibleItems.map((item, i) => {
          const idx = start + i;
          return (
            <div key={item.id} style={{ position: 'absolute', top: idx * rowHeight, left: 0, right: 0, height: rowHeight }} className="border-b px-3 py-2">
              {item.label}
            </div>
          );
        })}
      </div>
    </div>
  );
}

import React, { useEffect, useState } from 'react';

const KEY = 'sidebar-width';

export function DashboardLayout({ sidebar, children }) {
  const [width, setWidth] = useState(280);
  const [collapsed, setCollapsed] = useState(false);

  useEffect(() => {
    const saved = Number(localStorage.getItem(KEY));
    if (saved > 200 && saved < 500) setWidth(saved);
  }, []);

  useEffect(() => {
    localStorage.setItem(KEY, String(width));
  }, [width]);

  return (
    <div className="grid min-h-screen" style={{ gridTemplateColumns: collapsed ? '72px 1fr' : `${width}px 1fr` }}>
      <aside className="border-r bg-slate-50 p-3">{sidebar}</aside>
      <main className="p-5">{children}</main>
      <button className="fixed bottom-4 left-4 rounded bg-slate-900 px-3 py-2 text-white" onClick={() => setCollapsed((v) => !v)}>
        {collapsed ? 'Expand' : 'Collapse'}
      </button>
      {!collapsed && (
        <input
          type="range"
          min={220}
          max={420}
          value={width}
          onChange={(e) => setWidth(Number(e.target.value))}
          className="fixed bottom-4 left-28"
        />
      )}
    </div>
  );
}

import React, { useMemo, useState } from 'react';

const schema = [
  { id: 'name', label: 'Name', type: 'text', required: true },
  { id: 'email', label: 'Email', type: 'email', required: true },
  { id: 'budget', label: 'Budget', type: 'number', required: false }
];

export function FormBuilder() {
  const [values, setValues] = useState({});
  const [touched, setTouched] = useState({});

  const errors = useMemo(() => {
    const e = {};
    for (const f of schema) {
      if (f.required && !values[f.id]) e[f.id] = `${f.label} is required`;
      if (f.type === 'email' && values[f.id] && !String(values[f.id]).includes('@')) e[f.id] = 'Invalid email';
    }
    return e;
  }, [values]);

  return (
    <div className="grid gap-6 lg:grid-cols-2">
      <form className="space-y-4 rounded-xl border p-4">
        {schema.map((field) => (
          <label key={field.id} className="block">
            <span className="mb-1 block text-sm font-medium">{field.label}</span>
            <input
              type={field.type}
              value={values[field.id] ?? ''}
              onBlur={() => setTouched((t) => ({ ...t, [field.id]: true }))}
              onChange={(e) => setValues((v) => ({ ...v, [field.id]: e.target.value }))}
              className="w-full rounded border px-3 py-2"
            />
            {touched[field.id] && errors[field.id] && <p className="mt-1 text-sm text-red-600">{errors[field.id]}</p>}
          </label>
        ))}
      </form>
      <aside className="rounded-xl border bg-slate-50 p-4">
        <h4 className="mb-2 font-semibold">Live Preview</h4>
        <pre className="overflow-auto rounded bg-slate-900 p-3 text-xs text-slate-100">{JSON.stringify(values, null, 2)}</pre>
      </aside>
    </div>
  );
}
